const {composeTagString} = require("../../index");
const {defineGetter} = require("../utils/define-getter");
const openCloseTagPattern = '<([\\w-]+)([^<>\\/]*)>(.*?)<\\/?\\1>';
const selfCloseTagPattern = '<!?([\\w-]+)([^<>\\/]*)\\/?>';
const commentPattern = '<!--\\s+(.*?)\\s+-->';
const nonTagPattern = '^((?!<!?[\\w-]+[^<>\\/]+>(?:.*?<[^>]+>)?).)*';
const tagPattern = new RegExp(`${openCloseTagPattern}|${commentPattern}|${selfCloseTagPattern}|${nonTagPattern}`, 'gms');
// const tagPattern = /<([\w-]+)([^<>\/]*)>(.*?)<\/?\1>|<!--\s+(.*?)\s+-->|<!?([\w-]+)([^<>\/]*)\/?>|^((?!<!?[\w-]+[^<>\/]+>(?:.*?<[^>]+>)?).)*/gms;
const attributePattern = /([\#\w]+)(?:=(?="|'[^"]*"|)"([^"]*)")?/g;
const {parse} = require("node-html-parser");

class Node {
  #context = {};
  attributes = {};
  
  removeAttribute() {}
  
  setAttribute() {}
  
  removeContext() {}
  
  setContext() {}
}

class TextNode extends Node {
  type = 'TEXT';
  
  constructor(value, parentNode) {
    super();
    this.name = 'text';
    this.value = value;
    defineGetter(this, 'parentNode', parentNode)
  }
  
  toString() {
    return this.value;
  }
}

class HTMLNode extends Node {
  type = 'HTML';
  #attributes = null;
  // #childNodes = null;
  
  constructor(name, attributesString, innerHTML, parentNode) {
    super();
    this.name = name;
    this.parentNode = parentNode;
    this.textContent = ''; // todo: strip html tag away
    this.innerHTML = innerHTML;
    this.attributesText = attributesString;
    this.childNodes = parseHTMLString(this.innerHTML, this.parentNode);
    
    defineGetter(this, 'parentNode', parentNode)
  }
  
  get attributes() {
    if (this.#attributes === null) {
      this.#attributes = Object.create(null);
      
      for (let [_, key, value] of this.attributesText.matchAll(attributePattern)) {
        if (value) {
          this.#attributes[key] = value;
        } else {
          this.#attributes[key] = true;
        }
      }
    }
    
    return this.#attributes
  }
  
  toString() {
    return composeTagString({
      tagName: this.name,
      attributes: this.attributes
    }, this.innerHTML)
  }
}

class CommentNode {
  type = 'COMMENT';
  
  constructor(value, outerHTML, parentNode) {
    this.name = 'comment';
    this.value = value;
    this.outerHTML = outerHTML;
    defineGetter(this, 'parentNode', parentNode)
  }
  
  toString() {
    return this.outerHTML;
  }
}

class Document extends Node {
  type = 'DOCUMENT';
  
  constructor(innerHTML) {
    super()
    this.name = 'document';
    this.innerHTML = innerHTML;
    this.childNodes = parseHTMLString(innerHTML, this);
  }
  
  toString() {
    return this.innerHTML;
  }
}

function parseHTMLString(htmlString, parentNode = null) {
  if (!htmlString || !htmlString.trim()) {
    return []
  }
  
  htmlString = htmlString.trim();
  
  const tags = [];
  
  for (let match of htmlString.matchAll(tagPattern)) {
    if (!match[1] && !match[5]) { // no tag name
      if (match[0].startsWith('<!--')) {
        tags.push(new CommentNode(match[5], match[0] ?? '', parentNode))
      } else {
        tags.push(new TextNode(match[0], parentNode))
      }
    } else if(match[1]) { // open close tag - first part of the pattern
      tags.push(new HTMLNode(match[1], match[2] ?? '', match[3] ?? '', parentNode))
    } else { // self closing tag - last part of the pattern
      tags.push(new HTMLNode(match[5], match[6] ?? '', '', parentNode))
    }
  }
  
  return tags;
}

